import { useState } from 'react';
import data from './MovieData.json';

const MovieData = data.MovieData;

function MovieInfo() {
  const [listMovies, setListOfMovies] = useState(MovieData);
  const addMovie = () => {
    setListOfMovies([
      ...MovieData,
      {
        Title: 'The Godfather',
        Year: '1972',
        Director: 'Francis Ford Coppola',
        Rating: '9.2',
        Category: 'Crime, Drama',
      },
    ]);
  };

  return (
    <>
      <div>
        <div>
          <h1>Movie List</h1>
        </div>

        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Year</th>
              <th>Director</th>
              <th>Rating</th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            {MovieData.map((movie) => (
              <tr>
                <td>{movie.Title}</td>
                <td>{movie.Year}</td>
                <td>{movie.Director}</td>
                <td>{movie.Rating}</td>
                <td>{movie.Category}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button className="btn btn-primary" onClick={addMovie}>
        Add Movie
      </button>
    </>
  );
}

export default MovieInfo;
